jQuery(function($) {
    'use strict';

    var user = detect.parse(navigator.userAgent);

  
    (function() {
      var $frame = $('#basic');

  
      // Call Sly on frame
      $frame.sly({
        horizontal: 1,
        itemNav: 'basic',
      
        activateOn: 'click',
        mouseDragging: 1,
        touchDragging: 1,
        releaseSwing: 0,
   
      });
    }());
    $('.card__button').click(function(e) {
      e.preventDefault();
      $('#tovar').addClass('popup--active');

      $('#tovar .popup__inner').css('transform', 'translateY(0vh)');
      $('body').css({overflow: 'hidden'});
    });
    $('.popup__close, .goback').click(function(e) {
      e.preventDefault();
      
      $('#tovar').removeClass('popup--active');
      $('#auth_popup').removeClass('popup--active');
      $('#profile_popup').removeClass('popup--active');
      $('#adress_popup').removeClass('popup--active');
      $('#aboutBall').removeClass('popup--active');
      $('#tovar .popup__inner, #auth_popup .popup__inner').css('transform', 'translateY(-100vh)');
      $('body').css({'overflow-y': 'scroll'});

    });

    $('.auth, .mobile-menu__auth').click(function(e) {
      e.preventDefault();
      $('#auth_popup').addClass('popup--active');
      $('#auth_popup .popup__inner').css('transform', 'translateY(0vh)');

      $('#tovar .popup__inner').css('transform', 'translateY(0vh)')
    })

    $('#editProfileBtn').click(function(e) {
      e.preventDefault();
      $('#profile_popup').addClass('popup--active');
      $('#profile_popup .popup__inner').css('transform', 'translateY(0vh)')
    })

    $('#getPassword').click(function(e){
      e.preventDefault();
      $('#getPassword').text('Выслать еще раз');
      $('.auth-step_2').css('display', 'block');
      $('#getPasswordButton').addClass('black-border');

    });

    $('.edit_adress').click(function(e) {
      e.preventDefault();
      $('#adress_popup').addClass('popup--active');
      $('#adress_popup .popup__inner').css('transform', 'translateY(0vh)')
    });
    $('.about-bonuses').click(function(e) {
      e.preventDefault();
      $('#aboutBall').addClass('popup--active');
      $('#aboutBall .popup__inner').css('transform', 'translateY(0vh)')
    });
    $('.header__burger.white').click(function() {
      $('.mobile-menu').toggleClass('mobile-menu--active');
      
      $('.header__burger').toggleClass('burger--active');
    });

    $('.card__radio').on('change', function() {
     

      
        $('.card__label').removeClass('active');
        $(this).closest('.card__label').toggleClass('active');
      
    });

    $('.job__button, .jobs__button').click(function(e) {
      e.preventDefault();
      $('.jobs-popup').addClass('popup--active');
      $('.jobs-popup .popup__inner').css('transform', 'translateY(0vh)')
    })

    $('.order__more-btn').click(function(e) {
      e.preventDefault();
      let orderInfoTr = $(this).closest('tr').next(' .order__info');
      // let orderInfo = $(parentTr + '> .order__info');
      orderInfoTr.toggle(300);
      // console.log(parentTr);
    });

    $('.rolls-label').click(function() {
      let text = $(this).text();
      $('.rolls__title').text(text + " роллы");
    })
   

});

